import React, { useEffect, useState } from "react";
import axios from 'axios'; // Importez axios ici
import '../Css/Header.css'
import logo from './img_component/logo_crop_2.png'
import user from './img_component/user.png'

function Header() {
  let storedId = localStorage.getItem("userId");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Vérifiez si storedId est défini pour déterminer si l'utilisateur est connecté
    setIsLoggedIn(!!storedId);
  }, [storedId]);

  return (
    <header>
      <div className="logo">
        <img src={logo} alt=""/>
      </div>
      <nav>
        <ul>
          <li><a href="acceuil">Acceuil</a></li>
          <li><a href={isLoggedIn ? "login" : "message"}>{isLoggedIn ? "Messages" : "Messages"}</a></li>
          {/* <li><a href="message">Mes messages</a></li> */}
          <li><a href="historique">Recherche</a></li>
          <li><a href={isLoggedIn ? "logout" : "login"}>{isLoggedIn ? "Logout" : "Login"}</a></li>
          {/* <li><a href="recherche_terrain">Resultat</a></li> */}
        </ul>
        <div className="profile">
          <img src={user} alt=""/>
        </div>
      </nav>
    </header>
  );
}

export default Header;
